import {
    isObject
} from "../../../../../../core/utils/type";
export const isAllowedColumnValue = value => isObject(value) || "string" === typeof value;
export const isCorrectColumnIdx = pathIdx => !isNaN(+pathIdx) && null !== pathIdx;
export const getColumnIdxFromPath = path => +path[1];
export const getColumnOptionPathStr = path => [...path].splice(2).join(".");
